$(document).ready(function(){$("a").on("click",function(e){if(""!==this.hash){e.preventDefault();var n=this.hash;$("html, body").animate({scrollTop:$(n).offset().top},1e3,function(){window.location.hash=n})}})}),$(document).scroll(function(){$(document).scrollTop()>200?$("nav").addClass("shrink-nav"):$("nav").removeClass("shrink-nav")}),$(function(){$(document).on("change",":file",function(){var e=$(this),n=e.get(0).files?e.get(0).files.length:1,t=e.val().replace(/\\/g,"/").replace(/.*\//,"");e.trigger("fileselect",[n,t])}),$(document).ready(function(){$(":file").on("fileselect",function(e,n,t){var i=$(this).parents(".input-group").find(":text"),l=n>1?n+" files selected":t;i.length?i.val(l):l&&alert(l)})})});