$(document).ready(function(){$("a").on("click",function(e){if(""!==this.hash){e.preventDefault();var t=this.hash;$("html, body").animate({scrollTop:$(t).offset().top},1e3,function(){window.location.hash=t})}})}),$(function(){$(document).on("change",":file",function(){var e=$(this),t=e.get(0).files?e.get(0).files.length:1,n=e.val().replace(/\\/g,"/").replace(/.*\//,"");e.trigger("fileselect",[t,n])}),$(document).ready(function(){$(":file").on("fileselect",function(e,t,n){var i=$(this).parents(".input-group").find(":text"),l=t>1?t+" files selected":n;i.length?i.val(l):l&&alert(l)})})});